// Build gethomemeasure: J-1 00:00 (local) → maintenant, échelle 1h
const home_id  = global.get('home_id');
const bridge   = global.get('routeur_id');

const room_ids = [
  global.get('room0_id'), global.get('room1_id'), global.get('room2_id'),
  global.get('room3_id'), global.get('room4_id'), global.get('room5_id'),
  global.get('room6_id'), global.get('room7_id'), global.get('room8_id'),
  global.get('room9_id'), global.get('room10_id')
];

// --- TOKEN ---
const rawToken = global.get('AccessToken') || global.get('intuis_token') || global.get('token');
if (!rawToken) { node.error("Token Intuis introuvable"); return null; }
msg.token = rawToken.startsWith('Bearer ') ? rawToken : `Bearer ${rawToken}`;

// --- TZ cohérent ---
const tz = global.get('tz') || "Europe/Paris";

// "now" en local TZ
const nowLocal = new Date(new Date().toLocaleString('en-US', { timeZone: tz }));
// Minuit local aujourd’hui
const todayLocalMid = new Date(nowLocal); todayLocalMid.setHours(0,0,0,0);
// J-1 minuit local
const j2LocalMid = new Date(todayLocalMid); j2LocalMid.setDate(j2LocalMid.getDate()-1);

msg.payload = {
  date_begin: Math.floor(j2LocalMid.getTime()/1000),
  date_end:   Math.floor(nowLocal.getTime()/1000),
  scale: "1hour",
  app_identifier: "app_muller",
  home: {
    id: home_id,
    rooms: room_ids.map(id => ({
      id,
      bridge,
      type: "sum_energy_elec$0"
    }))
  }
};

return msg;

// === Prépare les imports (toujours envoyer, même sans changement) ===

// Map RoomID → statistic_id + nom facultatif
const MAP = {
  '3165697331': { id:'sensor.conso_entree_hourly',               name:"Conso Entrée Hourly" },
  '3330257038': { id:'sensor.conso_chambre_d_amis_hourly',       name:"Conso Chambre d'amis Hourly" },
  '2979072780': { id:'sensor.conso_salle_de_bain_filles_hourly', name:"Conso SDB Filles Hourly" },
  '3957213003': { id:'sensor.conso_chambre_filles_hourly',       name:"Conso Chambre Filles Hourly" },
  '3807033309': { id:'sensor.conso_couloir_hourly',              name:"Conso Couloir Hourly" },
  '3864339241': { id:'sensor.conso_toilettes_visiteurs_hourly',  name:"Conso WC Visiteurs Hourly" },
  '2064991252': { id:'sensor.conso_salon_hourly',                name:"Conso Salon Hourly" },
  '3761003540': { id:'sensor.conso_salle_a_manger_hourly',       name:"Conso Salle à manger Hourly" },
  '3357075329': { id:'sensor.conso_salle_de_bain_parents_hourly',name:"Conso SDB Parents Hourly" },
  '1668330398': { id:'sensor.conso_cuisine_hourly',              name:"Conso Cuisine Hourly" },
  '1707740468': { id:'sensor.conso_chambre_parents_hourly',      name:"Conso Chambre Parents Hourly" }
};

// Baselines (kWh) “théoriques” (EOD J-2, via rebase J-2)
const baseAll = global.get('base_kwh') || {};

// Réponse Intuis
const rooms = msg.payload?.body?.home?.rooms || [];
if (!rooms.length) { node.warn('Pas de rooms dans la réponse Intuis'); return null; }

const outMsgs = [];
const STORE = 'file';
function cget(k){ try { return context.get(k, STORE); } catch(e) { return context.get(k); } }
function cset(k,v){ try { context.set(k,v,STORE); } catch(e) { context.set(k,v); } }

// util: jour/heure locales
const tz = global.get('tz') || "Europe/Paris";
function localParts(ms) {
  const d = new Date(ms);
  const dayKey = new Intl.DateTimeFormat('en-CA', {timeZone: tz, year:'numeric', month:'2-digit', day:'2-digit'}).format(d); // YYYY-MM-DD
  const hour   = Number(new Intl.DateTimeFormat('fr-FR', {timeZone: tz, hour:'2-digit', hour12:false}).format(d)); // 0..23
  return { dayKey, hour };
}

const HOUR_MS = 3600 * 1000;

for (const r of rooms){
  const meta = MAP[r.id]; if (!meta) continue;
  const statId = meta.id;

  const m = r?.measures?.[0];
  const vals = Array.isArray(m?.value) ? m.value : [];
  if (!vals.length) continue;

  const stepSec  = Number(m?.step_time) || 3600;           // secondes
  const edge0Sec = Number(m?.beg_time) - stepSec/2;        // début du 1er créneau (s, UTC)

  // Ancre incrémentale persistée: pour continuité entre runs (ex: 23:00 -> 00:00)
  const prevSumKey   = `import_prev_sum_${statId}`;
  const prevStartKey = `import_prev_start_${statId}`;
  let prevSum   = Number(cget(prevSumKey));
  let prevStart = Number(cget(prevStartKey));

  // Base théorique (EOD J-2) si aucune ancre disponible
  const baseTheo = Number(baseAll[statId]);

  // Cumul depuis minuit (local) pour la journée courante (reset au changement de dayKey)
  let runKwhFromMidnight = 0;
  let lastDayKey = null;

  // Base de journée explicitement utilisée (pour idempotence si reimport de mêmes heures)
  const dayBaseCache = {}; // dayKey -> base kWh (ancre 00:00 de ce jour)

  const stats = [];
  for (let i = 0; i < vals.length; i++) {
    const v = Array.isArray(vals[i]) ? vals[i][0] : undefined;
    const n = (typeof v === 'number' && Number.isFinite(v)) ? v
          : (typeof v === 'string' && v.trim() !== '' && Number.isFinite(+v)) ? +v
          : NaN;
    if (!Number.isFinite(n)) continue;

    // --- TOP OF HOUR (HA exige mm:ss = 00:00) ---
    const startSecRaw = edge0Sec + i * stepSec;                 // secondes UTC
    const startMsSnap = Math.floor((startSecRaw * 1000) / HOUR_MS) * HOUR_MS;
    const d = new Date(startMsSnap);
    d.setUTCMinutes(0, 0, 0);                                   // 00:00.000
    const startMs  = d.getTime();
    const startISO = d.toISOString();

    // Gestion du jour local
    const { dayKey, hour } = localParts(startMs);
    if (lastDayKey === null || dayKey !== lastDayKey) {
      runKwhFromMidnight = 0;
      lastDayKey = dayKey;
    }
    if (!(dayKey in dayBaseCache)) {
      // Si on connaît l’ancre (ex: 23:00 d’hier), on s’en sert pour today 00:00
      if (hour === 0 && Number.isFinite(prevSum) && Number.isFinite(prevStart) && (startMs - prevStart === HOUR_MS)) {
        dayBaseCache[dayKey] = prevSum;
      } else {
        // Sinon: fallback sur baseTheo (EOD J-2) ou prevSum (continuité), ou 0
        dayBaseCache[dayKey] = Number.isFinite(baseTheo) ? baseTheo
                             : Number.isFinite(prevSum)  ? prevSum
                             : 0;
      }
    }

    // Ajout de la conso horaire (Wh -> kWh)
    runKwhFromMidnight += n / 1000;

    // Calcul sum: priorité à la continuité parfaite d’heure en heure,
    // sinon base de la journée + cumul depuis 00:00 local
    let sum;
    if (Number.isFinite(prevSum) && Number.isFinite(prevStart) && (startMs - prevStart === HOUR_MS)) {
      sum = Number((prevSum + (n/1000)).toFixed(3));
    } else {
      sum = Number((dayBaseCache[dayKey] + runKwhFromMidnight).toFixed(3));
    }

    // Toujours pousser le point, même si inchangé
    stats.push({ start: startISO, sum: sum, state: sum });

    // Avance l’ancre incrémentale pour le point suivant
    prevSum   = sum;
    prevStart = startMs;
  }

  if (!stats.length) continue;

  // MàJ des ancres persistées (pour le prochain run)
  const lastMs = Date.parse(stats[stats.length - 1].start) || 0;
  if (lastMs) {
    cset(prevStartKey, lastMs);
    cset(prevSumKey,   stats[stats.length - 1].sum);
  }

  const payload = {
    has_mean: false,
    has_sum: true,
    statistic_id: statId,
    source: "recorder",
    unit_of_measurement: "kWh",
    stats
  };

  outMsgs.push({
    payload,
    _preview: { sensor: statId, count: stats.length, first: stats[0].start, last: stats[stats.length-1].start }
  });
}

if (!outMsgs.length){
  node.status({fill:"green",shape:"dot",text:"rien à importer"});
  return null;
}
return [ outMsgs ];

