const rooms = msg.payload?.body?.home?.rooms || [];
if (!rooms.length) { node.warn('Aucune room Intuis'); return [null, {payload:'Aucun capteur'}]; }

const outMsgs = [];
const previews = [];
for (const r of rooms){
  const statId = MAP[r.id];
