    runKwh += n/1000; // cumul J-1
    const startISO = new Date(edge0 + i*step*1000).toISOString();
    const sum = Number((base + runKwh).toFixed(3));

    // IMPORTANT: envoyer sum + state
    stats.push({ start: startISO, sum: sum, state: sum });
  }

  if (!stats.length) continue;

  // total J-1 pour ce sensor (servira à "avancer" la baseline après import réussi)
  totalsYday[statId] = Number((runKwh).toFixed(3));

  outMsgs.push({
    payload: {
      has_mean: false,
      has_sum: true,
      statistic_id: statId,
      source: "recorder",
      unit_of_measurement: "kWh",
      stats
    },
    _preview: { sensor: statId, count: stats.length, first: stats[0].start, last: stats[stats.length-1].start }
  });
