];

// --- TOKEN ---
const rawToken = global.get('AccessToken') || global.get('intuis_token') || global.get('token');
if (!rawToken) { node.error("Token Intuis introuvable dans global.AccessToken / global.intuis_token / global.token"); return null; }
msg.token = rawToken.startsWith('Bearer ') ? rawToken : `Bearer ${rawToken}`;
// -------------

// Le conteneur est en Europe/Paris → on peut créer des Date locales:
// JS: mois 0-indexé (août = 7, octobre = 9)
const startLocal = new Date(2025, 7, 16, 0, 0, 0, 0); // 16/08/2025 00:00 local
const endLocal   = new Date(2025, 9, 7,  0, 0, 0, 0); // 07/10/2025 00:00 local (exclu) ⇒ inclut 06/10 entier

msg.payload = {
  date_begin: Math.floor(startLocal.getTime()/1000),
  date_end:   Math.floor(endLocal.getTime()/1000),
  scale: "1day",
  app_identifier: "app_muller",
  home: { id: home_id, rooms: room_ids.map(id => ({ id, bridge, type: "sum_energy_elec$0" })) }
};

// Pour info dans la suite
msg._period = { from: "2025-08-16", to: "2025-10-06" };
return msg;

// Agrège toutes les valeurs 1day (Wh) par room → kWh par sensor.
// Sortie en debug: { base_kwh: { sensor_id: kWh, ... }, from, to }

const MAP = {
  '3165697331':'sensor.conso_entree_hourly',
  '3330257038':'sensor.conso_chambre_d_amis_hourly',
  '2979072780':'sensor.conso_salle_de_bain_filles_hourly',
  '3957213003':'sensor.conso_chambre_filles_hourly',
  '3807033309':'sensor.conso_couloir_hourly',
  '3864339241':'sensor.conso_toilettes_visiteurs_hourly',
  '2064991252':'sensor.conso_salon_hourly',
  '3761003540':'sensor.conso_salle_a_manger_hourly',
  '3357075329':'sensor.conso_salle_de_bain_parents_hourly',
  '1668330398':'sensor.conso_cuisine_hourly',
  '1707740468':'sensor.conso_chambre_parents_hourly'
};

if (msg.statusCode && msg.statusCode !== 200){
  node.error(`HTTP ${msg.statusCode} sur gethomemeasure (1day, range)`);
  return null;
}

const rooms = msg.payload?.body?.home?.rooms || [];
if (!rooms.length) { node.warn('Pas de rooms dans la réponse Intuis'); return null; }

const totals = {}; // sensor_id -> kWh
for (const r of rooms){
  const sensor = MAP[r.id];
  if (!sensor) continue;
  let wh = 0;
  const measures = Array.isArray(r.measures) ? r.measures : [];
  for (const m of measures){
    const arr = Array.isArray(m.value) ? m.value : [];
    for (const row of arr){
      const v = Array.isArray(row) ? row[0] : row;
      const num = (typeof v === 'number' && Number.isFinite(v)) ? v
                : (typeof v === 'string' && v.trim()!=='' && Number.isFinite(+v)) ? +v
                : NaN;
      if (Number.isFinite(num)) wh += num; // Wh
    }
  }
  totals[sensor] = Number((wh/1000).toFixed(3)); // kWh
}

msg.payload = {
  from: msg._period?.from || null,
  to:   msg._period?.to   || null,
  base_kwh: totals
};
return msg;

const totals = msg?.payload?.base_kwh;
if (!totals || typeof totals !== 'object') {
  node.warn('Rebase SET: payload.base_kwh manquant');
  return null;
}

const r3 = (x) => Number((Number(x)||0).toFixed(3));

// état actuel
const before = Object.assign({}, global.get('base_kwh') || {});

// after = on REMPLACE par les valeurs fournies (SET)
const after = {};
for (const [sensor, val] of Object.entries(totals)) {
  after[sensor] = r3(val);
}

// delta = after - before (pour contrôle)
const delta = {};
const keys = new Set([...Object.keys(before), ...Object.keys(after)]);
for (const k of keys) {
  delta[k] = r3((Number(after[k])||0) - (Number(before[k])||0));
}

// commit
global.set('base_kwh', after);

msg.payload = {
  rebase_applied: true,
  mode: 'set',
  from: msg._period?.from || null,
  to:   msg._period?.to   || null,
  before,
  set_values: after,
  delta,
  after
};
return msg;

const tz = global.get('tz') || "Europe/Paris";
const STORE = 'file';
function cget(k){ try{ return context.get(k, STORE);}catch(e){return context.get(k);} }
function cset(k,v){ try{ context.set(k, v, STORE);}catch(e){ context.set(k,v);} }

// date du jour (locale)
const nowLocal = new Date(new Date().toLocaleString('en-US', { timeZone: tz }));
const todayStr = new Intl.DateTimeFormat('en-CA', { timeZone: tz, year:'numeric', month:'2-digit', day:'2-digit' }).format(nowLocal);
const lockKey  = `rebase_run_today_${todayStr}`;

// si déjà exécuté aujourd'hui (sauf override)
if (!msg.force && cget(lockKey)){
  node.status({ fill:'green', shape:'ring', text:`Déjà exécuté aujourd'hui (${todayStr})` });
  return null;
}

// pose le verrou du jour et continue
cset(lockKey, true);
msg._run_today_key = lockKey;
msg._run_today_str = todayStr;
return msg;
