const today = new Date();
today.setHours(0,0,0,0);
const beginLocal = new Date(today.getTime() - 60*24*3600*1000);
const endLocal   = new Date(today.getTime() - 10*24*3600*1000 - 1000);
msg.date_begin = Math.floor(beginLocal.getTime()/1000);
msg.date_end   = Math.floor(endLocal.getTime()/1000);
return msg;

const hd = msg.payload;
const home = hd && hd.body && Array.isArray(hd.body.homes) ? hd.body.homes[0] : null;
if (!home) { node.error('homesdata inattendu: body.homes[0] manquant.', msg); return null; }
const homeId = home.id;
const nmgModule = Array.isArray(home.modules) ? home.modules.find(m => m.type === 'NMG') : null;
const bridgeId = (nmgModule && nmgModule.id)
  || (Array.isArray(home.rooms) && home.rooms[0] && home.rooms[0].therm_relay)
  || homeId;
const roomIds = Array.isArray(home.rooms) ? home.rooms.map(r => r.id) : [];
if (!roomIds.length) { node.error('Aucune room dans homesdata.', msg); return null; }
msg.payload = {
  date_begin: msg.date_begin,
  date_end:   msg.date_end,
  scale: '1day',
  app_identifier: 'app_muller',
  home: { id: homeId, rooms: roomIds.map(id => ({ id, bridge: bridgeId, type: 'sum_energy_elec$0' })) }
};
return msg;

// Sortie : UN fichier CSV pour tous les sensors
// Format: statistic_id,start,state,sum   avec start = "dd.mm.yyyy 00:00" (Europe/Zurich)

const body = msg.payload?.body; const rooms = body?.home?.rooms || [];
if (!rooms.length) { return null; }

const exportDir = '/data/intuis/ha_import';

// mapping room -> sensor_id (HA)
const MAP = {
  '3165697331':'sensor.conso_entree_hourly',
  '3330257038':'sensor.conso_chambre_d_amis_hourly',
  '2979072780':'sensor.conso_salle_de_bain_filles_hourly',
  '3957213003':'sensor.conso_chambre_filles_hourly',
  '1707740468':'sensor.conso_chambre_parents_hourly',
  '3807033309':'sensor.conso_couloir_hourly',
  '1668330398':'sensor.conso_cuisine_hourly',
  '3761003540':'sensor.conso_salle_a_manger_hourly',
  '3357075329':'sensor.conso_salle_de_bain_parents_hourly',
  '2064991252':'sensor.conso_salon_hourly',
  '3864339241':'sensor.conso_toilettes_visiteurs_hourly'
};

function fmtStartDay(tsMs){
  const d = new Date(tsMs);
  const parts = new Intl.DateTimeFormat('fr-CH', { timeZone:'Europe/Zurich', year:'numeric', month:'2-digit', day:'2-digit' }).formatToParts(d);
  const get = t => parts.find(p => p.type === t)?.value || '';
  return `${get('day')}.${get('month')}.${get('year')} 00:00`;
}

const rows = [];       // {ts, sensorId, state, sum, startStr}
const baseMap = {};    // kWh cumulés fin J-5

for (const r of rooms){
  const sensorId = MAP[r.id]; if (!sensorId) continue;
  const m = r.measures && r.measures[0]; if (!m || !Array.isArray(m.value) || !m.value.length) continue;
  const step = m.step_time || 86400; const edge0 = m.beg_time - step/2; // 00:00 local
  let cumWh = 0;
  for (let i=0;i<m.value.length;i++){
    const tsEdge = (edge0 + i*step) * 1000;
    const wh = Number(m.value[i][0]) || 0; cumWh += wh;
    const state = (cumWh/1000); const sum = (cumWh/1000);
    rows.push({ ts: tsEdge, sensorId, state, sum, startStr: fmtStartDay(tsEdge) });
  }
  baseMap[sensorId] = Number((cumWh/1000).toFixed(3));
}

rows.sort((a,b)=> a.ts - b.ts || (a.sensorId < b.sensorId ? -1 : 1));

const lines = ['statistic_id,start,state,sum'];
for (const r of rows){ lines.push(`${r.sensorId},${r.startStr},${r.state.toFixed(3)},${r.sum.toFixed(3)}`); }

const d0 = new Date(msg.date_begin*1000).toISOString().slice(0,10).replace(/-/g,'');
const d1 = new Date(msg.date_end*1000).toISOString().slice(0,10).replace(/-/g,'');

// expose la base fin J-5
global.set('ha_stat_base_90_end', baseMap);

return [{ payload: lines.join('\n')+'\n', filename: `${exportDir}/ha_import_daily.csv` }];

// Récupère le token Intuis depuis global et prépare msg.token (Bearer ...)
const t = global.get('AccessToken') || global.get('token');
if (!t) {
  node.error('Token Intuis introuvable dans global.intuis_token / global.token');
  return null;
}
msg.token = t.startsWith('Bearer ') ? t : `Bearer ${t}`;
return msg;

const today = new Date();
today.setHours(0,0,0,0); // 00:00 local aujourd'hui
const beginLocal = new Date(today.getTime() - 10*24*3600*1000); // J-4 00:00
const endLocal   = new Date(today.getTime());        // hier 23:59:59
msg.date_begin = Math.floor(beginLocal.getTime()/1000);
msg.date_end   = Math.floor(endLocal.getTime()/1000);
return msg;

const hd = msg.payload;
const home = hd && hd.body && Array.isArray(hd.body.homes) ? hd.body.homes[0] : null;
if (!home) { node.error('homesdata inattendu: body.homes[0] manquant.', msg); return null; }
const homeId = home.id;
const nmgModule = Array.isArray(home.modules) ? home.modules.find(m => m.type === 'NMG') : null;
const bridgeId = (nmgModule && nmgModule.id)
  || (Array.isArray(home.rooms) && home.rooms[0] && home.rooms[0].therm_relay)
  || homeId;
const roomIds = Array.isArray(home.rooms) ? home.rooms.map(r => r.id) : [];
if (!roomIds.length) { node.error('Aucune room dans homesdata.', msg); return null; }
msg.payload = {
  date_begin: msg.date_begin,
  date_end:   msg.date_end,
  scale: '1hour',
  app_identifier: 'app_muller',
  home: { id: homeId, rooms: roomIds.map(id => ({ id, bridge: bridgeId, type: 'sum_energy_elec$0' })) }
};
return msg;

// Entrées:
//  - msg.measure = réponse /gethomemeasure (1h) pour J-4 → J-1
//  - msg.payload = contenu texte du ha_import_daily.csv (si dispo)
// Sortie:
//  - /data/intuis/ha_import/ha_import_hourly.csv

const MAP = {
  '3165697331':'sensor.conso_entree_hourly',
  '3330257038':'sensor.conso_chambre_d_amis_hourly',
  '2979072780':'sensor.conso_salle_de_bain_filles_hourly',
  '3957213003':'sensor.conso_chambre_filles_hourly',
  '1707740468':'sensor.conso_chambre_parents_hourly',
  '3807033309':'sensor.conso_couloir_hourly',
  '1668330398':'sensor.conso_cuisine_hourly',
  '3761003540':'sensor.conso_salle_a_manger_hourly',
  '3357075329':'sensor.conso_salle_de_bain_parents_hourly',
  '2064991252':'sensor.conso_salon_hourly',
  '3864339241':'sensor.conso_toilettes_visiteurs_hourly'
};

function fmtLocal(tsMs){
  const parts = new Intl.DateTimeFormat('fr-CH',{
    timeZone:'Europe/Zurich',year:'numeric',month:'2-digit',day:'2-digit',hour:'2-digit',minute:'2-digit',hour12:false
  }).formatToParts(new Date(tsMs));
  const get=t=>parts.find(p=>p.type===t)?.value||'';
  return `${get('day')}.${get('month')}.${get('year')} ${get('hour')}:${get('minute')}`;
}

// 1) baseSums depuis le DAILY (max sum par sensor)
const baseSums = {}; // kWh
try {
  const text = typeof msg.payload === 'string' ? msg.payload : '';
  if (text) {
    const lines = text.trim().split(/\r?\n/);
    for (const line of lines.slice(1)) {
      const parts = line.split(',');
      if (parts.length < 4) continue;
      const sensor = parts[0];
      const sum = Number(parts[3]);
      if (!Number.isFinite(sum)) continue;
      baseSums[sensor] = Math.max(baseSums[sensor] || 0, sum);
    }
  }
} catch(e){ node.warn('Lecture daily.csv impossible: '+e.message); }

// 2) mesures horaires
const body = msg.measure?.body; const rooms = body?.home?.rooms || [];
if (!rooms.length) return null;

const lines = ['statistic_id,start,state,sum'];
for (const r of rooms){
  const sensorId = MAP[r.id]; if (!sensorId) continue;
  const m = r?.measures?.[0]; if (!m || !Array.isArray(m.value) || !m.value.length) continue;
  const step = m.step_time || 3600;          // 1h
  const edge0 = m.beg_time - step/2;         // H:00 local

  // cumul continu: on part du dernier cumul du daily
  let totalWh = (baseSums[sensorId] || 0) * 1000; // kWh -> Wh

  for (let i=0;i<m.value.length;i++){
    const tsEdgeMs = (edge0 + i*step) * 1000;
    const wh = Number(m.value[i][0]) || 0; totalWh += wh;
    const kwh = totalWh/1000;
    const startStr = fmtLocal(tsEdgeMs);
    lines.push(`${sensorId},${startStr},${kwh.toFixed(3)},${kwh.toFixed(3)}`);
  }
}

msg.payload = lines.join('\n')+'\n';
msg.filename = '/data/intuis/ha_import/ha_import_hourly.csv';
return msg;

// Récupère le token Intuis depuis global et prépare msg.token (Bearer ...)
const t = global.get('AccessToken') || global.get('token');
if (!t) {
  node.error('Token Intuis introuvable dans global.intuis_token / global.token');
  return null;
}
msg.token = t.startsWith('Bearer ') ? t : `Bearer ${t}`;
return msg;

// Lit msg.payload (CSV texte) et charge base_kwh = somme à "hier 00:00" (Europe/Zurich)
// CSV format: statistic_id,start,state,sum   avec start "dd.mm.yyyy HH:MM"

const text = (typeof msg.payload === 'string') ? msg.payload : '';
if (!text) { node.warn('CSV vide'); return null; }

const lines = text.trim().split(/\r?\n/);
if (lines.length <= 1) { node.warn('CSV: seulement l’en-tête ?'); return null; }

const header = lines[0].toLowerCase();
if (!(header.includes('statistic_id') && header.includes('start') && header.includes('sum'))) {
  node.error('Entête inattendu (attendu: statistic_id,start,state,sum)'); 
  return null;
}

// construit la chaîne "hier 00:00" au format du CSV (Europe/Zurich)
const tz = 'Europe/Zurich';
const now = new Date();
const todayLocal = new Intl.DateTimeFormat('fr-CH', { timeZone: tz, year:'numeric', month:'2-digit', day:'2-digit' })
                     .formatToParts(now);
function toLocalMidnightYesterdayString(){
  // calcule "hier" local
  const d = new Date(now);
  // passe à 00:00 local d'aujourd'hui puis -1 jour
  const today00 = new Date(d);
  // remet en 00:00 local par décalage de l'objet Date "naif"
  today00.setHours(0,0,0,0);
  const y = new Date(today00.getTime() - 24*3600*1000 );
  const parts = new Intl.DateTimeFormat('fr-CH', {
    timeZone: tz, year:'numeric', month:'2-digit', day:'2-digit',
    hour:'2-digit', minute:'2-digit', hour12:false
  }).formatToParts(y);
  const get = t => parts.find(p => p.type===t)?.value || '';
  // IMPORTANT: on force "00:00"
  const dd = get('day'), mm = get('month'), yyyy = get('year');
  return `${dd}.${mm}.${yyyy} 23:00`;
}
const targetStart = toLocalMidnightYesterdayString();

const base = {};
for (let i=1;i<lines.length;i++){
  const line = lines[i].trim(); if (!line) continue;
  const [id,start, stateS, sumS] = line.split(',');
  if (!id || !start || sumS===undefined) continue;
  if (start.trim() !== targetStart) continue;
  let sum = Number(sumS);
  if (!Number.isFinite(sum)) {
    const st = Number(stateS);
    if (Number.isFinite(st)) sum = st;
  }
  if (!Number.isFinite(sum)) continue;
  base[id.trim()] = Number(sum.toFixed(3));
}

// Sauvegarde globale (store file si dispo)
const STORE = 'file';
try { global.set('base_kwh', base, STORE); } catch(e) { global.set('base_kwh', base); }

node.status({fill:'green',shape:'dot',text:`baseline (avant-hier soir) chargée: ${Object.keys(base).length} sensors`});
return null;
